let consoleInfo = console.info;
console.info = function (...arg) {
	return consoleInfo(arg.join(' ').green);
};

let consoleErr = console.error;
console.error = function (...arg ) {
	return consoleInfo(arg.join(' ').red);
};

let consoleWarn = console.warn;
console.warn = function (...arg ) {
	return consoleInfo(arg.join(' ').yellowBG);
};

let consoleDebug = console.debug;
console.debug = function (...arg) {
	return consoleInfo(arg.join(' ').blue);
};

module.exports = {
	consoleInfo,
	consoleErr,
	consoleWarn,
	consoleDebug
};